const zones=[{id:"zone-a",name:"Zone A",currentLoad:100,capacity:100},{id:"zone-b",name:"Zone B",currentLoad:25,capacity:100},{id:"zone-c",name:"Zone C",currentLoad:Math.floor(101*Math.random()),capacity:100}];function initZonesAndTooltip(){document.querySelectorAll(".zone").forEach(t=>{var e=zones.find(e=>e.id===t.id),n=(t.dataset.name=e.name,t.dataset.currentLoad=e.currentLoad,t.dataset.capacity=e.capacity,e.currentLoad===e.capacity),e=e.currentLoad>=.75*e.capacity;t.classList.add(n?"full":e?"warning":"all-good"),t.addEventListener("mouseenter",onMouseEnterZone),t.addEventListener("mouseleave",onMouseLeaveZone)})}function onMouseEnterZone(e){var t=document.getElementById("zone-tooltip"),e=e.target,n=(t.classList.add("active"),e.getBoundingClientRect()),o=n.left+n.width/2,n=n.top+n.height/2;t.style.left=o+"px",t.style.top=n+"px",t.querySelector("h4").textContent=e.dataset.name,t.querySelector(".zone-tooltip__containers > span").textContent=e.dataset.currentLoad,t.querySelector(".zone-tooltip__capacity > span").textContent=e.dataset.capacity}function onMouseLeaveZone(){document.getElementById("zone-tooltip").classList.remove("active")}document.addEventListener("DOMContentLoaded",function(){console.log("DOM fully loaded and parsed"),initZonesAndTooltip()});